INSERT INTO `apps_feed_translation` (`tf_id`, `lang_id`, `tf_name`, `tf_annotation_rtf`, `tf_text_rtf`) VALUES
(1, 1, 'Проект запуска Energine на ZX Spectrum', 'Впервые нам удалось запустить nginx-lite версию Energine на ZX-Spectrum совместимом компьютере под управлением прошивки Pentagon 1024SL.', 'На нашем тестовом сервере наконец-то завершилась сборка ядра версии 3.15.168.dev после почти 7 лет непрерывной разработки для запуска на игровом компьютере ZX Spectrum.\nTape-образ будет доступен для скачивания немного позже.\nЖдите подробностей в ленте новостей в проекте starter!'),
(1, 2, 'Проект запуска Energine на ZX Spectrum', 'Впервые нам удалось запустить nginx-lite версию Energine на ZX-Spectrum совместимом компьютере под управлением прошивки Pentagon 1024SL.', 'На нашем тестовом сервере наконец-то завершилась сборка ядра версии 3.15.168.dev после почти 7 лет непрерывной разработки для запуска на игровом компьютере ZX Spectrum.\nTape-образ будет доступен для скачивания немного позже.\nЖдите подробностей в ленте новостей в проекте starter!'),
(2, 1, 'Проект Energine Starter', 'Краткое описание проекта Energine Starter', '<p>Проект <b>Energine Starter</b> является частью ядра и позволяет создавать новые проекты на базе Energine, используя данный проект как скелет будущего приложения.</p>\n<p>Проект имеет собственную структуру директорий для быстрого развертывания нового сайта. Перечень директорий:</p>\n<ul>\n<li>htdocs/core - виртуальная директория для прилинковки модулей ядра, указанных в конфигурационном файле system.config.php</li>\n<li>htdocs/site - директория, которая относится непосредственно к проекту. Содержит XML/XSLT файлы, принадлежащие проекту, а также код компонентов и их конфигурации.</li>\n<li>htdocs/index.php - основная точка входа</li>\n<li>htdocs/bootstrap.php - файл с начальной конфигурацией и настойкой ядра.</li>\n<li>htdocs/auth.php - файл для авторизации в социальной сети Facebook</li>\n<li>htdocs/resizer - директория со сторонней библиотекой масштабирования изображений</li>\n</ul> '),
(2, 2, 'Проект Energine Starter', 'Краткое описание проекта Energine Starter', '<p>Проект <b>Energine Starter</b> является частью ядра и позволяет создавать новые проекты на базе Energine, используя данный проект как скелет будущего приложения.</p>\n<p>Проект имеет собственную структуру директорий для быстрого развертывания нового сайта. Перечень директорий:</p>\n<ul>\n<li>htdocs/core - виртуальная директория для прилинковки модулей ядра, указанных в конфигурационном файле system.config.php</li>\n<li>htdocs/site - директория, которая относится непосредственно к проекту. Содержит XML/XSLT файлы, принадлежащие проекту, а также код компонентов и их конфигурации.</li>\n<li>htdocs/index.php - основная точка входа</li>\n<li>htdocs/bootstrap.php - файл с начальной конфигурацией и настойкой ядра.</li>\n<li>htdocs/auth.php - файл для авторизации в социальной сети Facebook</li>\n<li>htdocs/resizer - директория со сторонней библиотекой масштабирования изображений</li>\n</ul> ');
