INSERT INTO `apps_news_translation` (`news_id`, `lang_id`, `news_title`, `news_announce_rtf`, `news_text_rtf`) VALUES
(1, 1, 'Глобальное обновление Energine', 'Рады сообщить о выпуске новой версии Energine 2.11.1.beta.&nbsp;', 'Что нового в Energine, начиная с версии 2.11 ?<div><br></div><div><ol><li>Поддержка новой структуры проекта. Ядра теперь подключаются к проекту динамически через конфигурационный файл. Забудьте про svn:externals</li><li>Из ядра убрана директория core/kernel, весь код из нее переехал в core/modules/share/gears</li><li>Добавлены новые типы медиа-хранилищ и интерфейс IFileRepository для реализации своих типов</li><li>Обновленный starter со свежим и актуальным дампом базы данных и поддержкой новой структуры проекта</li><li>Много мелких баг-фиксов и улучшений в ядре</li><li>Обновлены примеры конфигурационных файлов для nginx и apache</li><li>Добавлен минималистичный readme.txt с инструкциями по базовому развертыванию</li></ol></div>'),
(1, 2, 'Ласкаво просимо', 'Анонс', 'Текст'),
(2, 1, 'Вышел GCC 4.8.0', 'Вышла новая версия набора компиляторов GNU — 4.8.0. ', '<p>Вышла новая версия набора компиляторов GNU — 4.8.0. Среди нововведений:</p>\n<ul>\n<li>Теперь для сборки GCC требуется компилятор С++.</li>\n<li>Формат отладочной информации по умолчанию изменён на DWARF4, который поддерживается в GDB 7.5, Valgrind 3.8.0 и elfutils 0.154.</li>\n<li>Добавлен новый уровень оптимизации -Og, предназначенный для быстрой компиляции и отладки, обеспечивая при этом приемлемую производительность.</li>\n<li>Убран ряд узких мест в оптимизаторе, что существенно ускорило компиляцию чрезвычайно больших функций.</li>\n<li>Добавлены AddressSanitizer (быстрый детектор ошибок работы с памятью) и ThreadSanitizer (детектор гонок данных).</li>\n<li>Улучшены сообщения об ошибках, исходная строка выводится полностью, а колонка с ошибкой отмечается знаком ^. Опция ftrack-macro-expansion=2 теперь включена по умолчанию, что показывает стек раскрытия макросов</li>\n<li>Улучшена поддержка стандарта C++11: поддерживается ключевое слово thread_local, синтаксис атрибутов, спецификаторов выравнивания и наследование конструкторов. Также добавлена опция -std=C++1y для поддержки будущего стандарта, пока она добавляет только возможность автоматического определения типа результата в определении функций.</li>\n<li>Реализована предварительная версия языка Go 1.1.</li>\n<li>Добавлена поддержка архитектуры AArch64.</li>'),
(2, 2, 'Вышел GCC 4.8.0', 'Вышла новая версия набора компиляторов GNU — 4.8.0. ', '<p>Вышла новая версия набора компиляторов GNU — 4.8.0. Среди нововведений:</p>\n<ul>\n<li>Теперь для сборки GCC требуется компилятор С++.</li>\n<li>Формат отладочной информации по умолчанию изменён на DWARF4, который поддерживается в GDB 7.5, Valgrind 3.8.0 и elfutils 0.154.</li>\n<li>Добавлен новый уровень оптимизации -Og, предназначенный для быстрой компиляции и отладки, обеспечивая при этом приемлемую производительность.</li>\n<li>Убран ряд узких мест в оптимизаторе, что существенно ускорило компиляцию чрезвычайно больших функций.</li>\n<li>Добавлены AddressSanitizer (быстрый детектор ошибок работы с памятью) и ThreadSanitizer (детектор гонок данных).</li>\n<li>Улучшены сообщения об ошибках, исходная строка выводится полностью, а колонка с ошибкой отмечается знаком ^. Опция ftrack-macro-expansion=2 теперь включена по умолчанию, что показывает стек раскрытия макросов</li>\n<li>Улучшена поддержка стандарта C++11: поддерживается ключевое слово thread_local, синтаксис атрибутов, спецификаторов выравнивания и наследование конструкторов. Также добавлена опция -std=C++1y для поддержки будущего стандарта, пока она добавляет только возможность автоматического определения типа результата в определении функций.</li>\n<li>Реализована предварительная версия языка Go 1.1.</li>\n<li>Добавлена поддержка архитектуры AArch64.</li>'),
(4, 1, 'Ричард Столлман против C#', 'Ричард Столлман (Richard M. Stallman) выступил с предостережением против использования среды программирования Mono для создания свободных приложений.', '<p>Ричард Столлман (Richard M. Stallman) выступил с предостережением против использования среды программирования Mono для создания свободных приложений.</p>\n\n<p>Опасность, по мнению FSF, заключается в том, что Microsoft, возможно, захочет перевести свободную реализацию C# на нелегальное положение, обвинив в нарушении каких-либо своих патентов. В этой связи FSF настоятельно рекомендует воздерживаться от использования C#, т.к. если этот язык будет объявлен вне закона, то будут потеряны все написанные на нем приложения. Поэтому использование C# для разработки свободных программ является рискованным.</p>\n\n<p>С этим утверждением категорически не согласны многие разработчики, использующие Mono. Они, в частности, указывают, что, несмотря на отсутствие патентной защиты, судебный иск со стороны Microsoft маловероятен, технические же преимущества Mono весьма велики.</p>\n\n<p>Оригинал новости: <a href="http://linuxcenter.ru" target="_blank">linuxcenter.ru</a></p>'),
(4, 2, 'Ричард Столлман против C#', 'Ричард Столлман (Richard M. Stallman) выступил с предостережением против использования среды программирования Mono для создания свободных приложений.', '<p>Ричард Столлман (Richard M. Stallman) выступил с предостережением против использования среды программирования Mono для создания свободных приложений.</p>\n\n<p>Опасность, по мнению FSF, заключается в том, что Microsoft, возможно, захочет перевести свободную реализацию C# на нелегальное положение, обвинив в нарушении каких-либо своих патентов. В этой связи FSF настоятельно рекомендует воздерживаться от использования C#, т.к. если этот язык будет объявлен вне закона, то будут потеряны все написанные на нем приложения. Поэтому использование C# для разработки свободных программ является рискованным.</p>\n\n<p>С этим утверждением категорически не согласны многие разработчики, использующие Mono. Они, в частности, указывают, что, несмотря на отсутствие патентной защиты, судебный иск со стороны Microsoft маловероятен, технические же преимущества Mono весьма велики.</p>\n\n<p>Оригинал новости: <a href="http://linuxcenter.ru" target="_blank">linuxcenter.ru</a></p>'),
(5, 1, 'Linux.fm — онлайн-радио, вещающее исходный код ядра Linux', 'В интернете появилось интернет-радио, которое зачитывает исходный код ядра Linux.', '<p>В интернете появилось интернет-радио, которое зачитывает исходный код ядра Linux.\nПри заходе на сайт Linux.fm система выбирает случайный файл из последнего стабильного ядра Linux (на данный момент это 2.6.36.1) и начинает воспроизводить его содержимое «человеческим» голосом с помощью свободного синтезатора речи eSpeak.</p>\n\n<p>Внизу главной (и единственной) страницы сайта написано, что «радиостанция посвящена величайшему в истории ученому — доктору Шелдону Куперу из Теории большого взрыва» (авторы имеют в виду известного персонажа популярного американского ситкома про физиков).</p>\n<p>Источник: <a href="http://www.nixp.ru/news/10920.html" target="_blank">http://www.nixp.ru/news/10920.html</a>.</p>'),
(5, 2, 'Linux.fm — онлайн-радио, вещающее исходный код ядра Linux', 'В интернете появилось интернет-радио, которое зачитывает исходный код ядра Linux.', '<p>В интернете появилось интернет-радио, которое зачитывает исходный код ядра Linux.\nПри заходе на сайт Linux.fm система выбирает случайный файл из последнего стабильного ядра Linux (на данный момент это 2.6.36.1) и начинает воспроизводить его содержимое «человеческим» голосом с помощью свободного синтезатора речи eSpeak.</p>\n\n<p>Внизу главной (и единственной) страницы сайта написано, что «радиостанция посвящена величайшему в истории ученому — доктору Шелдону Куперу из Теории большого взрыва» (авторы имеют в виду известного персонажа популярного американского ситкома про физиков).</p>\n<p>Источник: <a href="http://www.nixp.ru/news/10920.html" target="_blank">http://www.nixp.ru/news/10920.html</a>.</p>');
