un" style="color: rgb(102, 102, 0); ">=</span><span class="str" style="color: rgb(0, 136, 0); ">"component[@class=''MainMenu'']"</span><span class="pun" style="color: rgb(102, 102, 0); ">&gt;</span><span class="pln"><br>&nbsp; &nbsp; &nbsp; &nbsp; </span><span class="pun" style="color: rgb(102, 102, 0); ">&lt;</span><span class="pln">xsl</span><span class="pun" style="color: rgb(102, 102, 0); ">:</span><span class="pln">apply</span><span class="pun" style="color: rgb(102, 102, 0); ">-</span><span class="pln">templates</span><span class="pun" style="color: rgb(102, 102, 0); ">/&gt;</span><span class="pln"><br>&nbsp; &nbsp; </span><span class="pun" style="color: rgb(102, 102, 0); ">&lt;</span><span class="str" style="color: rgb(0, 136, 0); ">/xsl:template&gt;<br>&nbsp; &nbsp; <br>&nbsp; &nbsp; &lt;xsl:template match="recordset[parent::component[@class=''MainMenu'']]"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &lt;xsl:if test="not(@empty)"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;ul class="main_menu"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;xsl:if test="$DOC_PROPS[@name=''default''] != 1"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a href="{$LANG_ABBR}"&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;xsl:value-of select="$TRANSLATION[@const=''TXT_HOME'']" disable-output-escaping="yes"/</span><span class="pun" style="color: rgb(102, 102, 0); ">&gt;</span><span class="pln"><br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span><span class="pun" style="color: rgb(102, 102, 0); ">&lt;</span><span class="str" style="color: rgb(0, 136, 0); ">/a&gt;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/</span><span class="pln">li</span><span class="pun" style="color: rgb(102, 102, 0); ">&gt;